import React, { Fragment, useEffect } from 'react'
import { useState } from 'react'

function Skills() {
    const skills = ["C#", "Java", "JavaScript", "Python","HTML", "CSS","Bootstrap", "SDLC", "Git"]
    const [programmingLang , setProgrammingLang] = useState('Spring Boot')

    //select the arrays elements every 5 seconds
   const changeLanguage = async () => {
        let random = Math.floor(Math.random() * skills.length)
       await setProgrammingLang([programmingLang[random]])
    }

    useEffect(() => {
        changeLanguage()
    }, [])

    setInterval(changeLanguage, 5000)

    return (
        <Fragment>
             <h2 id="familiar" className="bg-dark text-center text-white">
                    I'm familiar with : <span>{programmingLang}</span>
                  </h2>
        </Fragment>
    )
}

export default Skills
