import React, { Fragment, useEffect } from 'react'
import { useState } from 'react'

function Skills() {
    const skills = ["C#", "Java", "JavaScript", "Python","HTML", "CSS","Bootstrap", "SDLC", "Git"]


    //select the arrays elements every 5 seconds

        let random = Math.floor(Math.random() * skills.length)


    return (
        <Fragment>
             <h2 id="familiar" className="bg-dark text-center text-white">
                    I'm familiar with : 
                    {
                      skills.map(skill => {
                          return <span>{skill[random]}</span>
                      })
                    }
                  </h2>
        </Fragment>
    )
}

export default Skills
